import UIKit

class AppData {
    
    static var animeList = [Anime]()
    
    static func createAnime() {
        
        var haikyuuCharacters = [Character]()
        haikyuuCharacters.append(Character(name: "Kageyama Tobio", discription: "дейтерагонист Haikyuu!! В течение большей части манги он был первогодкой старшей школы Карасуно, играя в качестве стартового связующего в мужском волейбольном клубе. Поначалу его называли «королем площадки» – уничижительное прозвище, связанное с его диктаторским присутствием в команде, которое ему дали сокомандники в Китагава Дайити. Многие игроки и тренеры префектуры считали его выдающимся волейболистом. После прихода в Карасуно ему удается изменить свое прежнее эгоцентричное отношение к лучшему с помощью своих новых товарищей по команде и «заново открыть», что значит быть частью команды.Он был выбран для обучения во всеяпонском молодежном тренировочном лагере во время своего первого года обучения в старшей школе и присоединился к V лиге, как только окончил ее. Он стал частью национальной сборной Японии по волейболу в возрасте 19 лет, участвуя в Олимпийских играх в Рио-де-Жанейро в 2016 году. В настоящее время он играет в качестве связующего за японскую команду V-League Division 1 Schweiden Adlers.", image:
                                            R.image.tobio()!))
        haikyuuCharacters.append(Character(name: "Bokuto Kotaro", discription: "третьегодка Академии Фукуродани, играющий на позиции доигровщика, а также ас и капитан волейбольной команды. Один из пяти лучших асов в стране. Является нападающим команды MSBY Black Jackals, команды 1-го дивизиона японской V-Лиги.", image: R.image.bokuto()!))
        haikyuuCharacters.append(Character(name: "Akaashi Keiji", discription: "второгодка Академии Фукуродани. Также он является вице-капитаном и связующим волейбольного клуба. По состоянию на ноябрь 2018 года он в настоящее время работает редактором в журнале «Сёнэн манга» от крупного издателя, хотя на самом деле он хотел быть в отделе литературы.", image: R.image.akaashi()!))
        
        var rebornCharacters = [Character]()
